import {IRegister} from "./interfaces";
import SsbRegis from "./methods";

export default
class Plugin {
    constructor() {
        this.register.attributes = {
           "pkg": {
                "name": "mypartner-plugin",
                "version": "1.0.0"
            }
        };
    }

    register : IRegister = (server, options, next) => {
        let SsbRegisVar : SsbRegis = new SsbRegis(server, options);
        SsbRegisVar.init();
        next();
    };
    errorInit(error) {
        if (error) {
     console.log('Error: Failed to load plugin ('+ this.register.attributes.pkg.name+'):',error);
        }
    }
}
