import * as Hapi from "hapi";

export interface IRegister {
    (server : Hapi.Server, options : any[], next : () => void) : void;
    attributes?: any;
}

export interface IHandler {
    (request : Hapi.Request, reply : Hapi.IReply) : void;
}

export interface IRequest {
    id : string;
    ip : string;
    apiRequestId : string;
}

export interface IProfile {
    mobileStatus : string;
    registerDate?: string;
    serviceYear?: {
        year: string;
        month: string;
        day: string;
    };
    remark : string;
    chargeType : string;
    customerType : string;
    birthDate : string;
}

export interface IActivity {
    createdDate : string;
    subCategory : string;
    status : string;
}