import {IRegister} from "./interfaces";
import * as Routes from "./routes";

export default
class Greeting {
    constructor() {
        this.register.attributes = {
           "pkg": {
                "name": "mypartner-route",
                "version": "1.0.0"
                }
        };
    }

    register : IRegister = (server, options, next) => {
        Routes.init(server);
        next();
    };

    errorInit(error) {
        if (error) {
         console.log('Error: Failed to load plugin (' + this.constructor.name + '):', error);
        }
    }
}